import Link from 'next/link';
import React from 'react';
import fetch from 'isomorphic-unfetch';
const baseUrl = 'http://localhost:4000/';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { ColumnGroup } from 'primereact/columngroup';
import { Row } from 'primereact/row';

export default function Dashboard({ data }) {
	return (
		<div>
			<Link href="/">
				<a title="New Policy">Create new policy</a>
			</Link>
			{/* <ul>{data.map((policy) => <div key={policy.id}>{policy.first_name}</div>)}</ul> */}
			<DataTable value={data}>
				<Column field="first_name" header="First Name" style={{ height: '3.5em' }} />
				<Column field="last_name" header="Last Name" />
				<Column field="email_address" header="Email Address" />
				<Column field="phone_number" header="Phone Number" />
				<Column field="company_name" header="Company Name" />
				<Column field="effective_date" header="Effective Date" />
			</DataTable>
		</div>
	);
}

export async function getServerSideProps() {
	const res = await fetch(`${baseUrl}policies`);
	const data = await res.json();
	return { props: { data } };
}
